import { useState } from 'react';
import './../../App.css';
import { Button, Container, Navbar } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';

const Header = ({ data }) => {
<pre>{process.env.REACT_APP_SECRET_NAME}</pre>
    const [active, setActive] = useState("");
 
  const handleClick = (event) => {
    setActive(event.target.innerText);
    
  }

    return (
        <div className="header-image">
            <div className="App-header">
                {/* <img src   alt="logo" /> */}
                <p className='header-text text-white p-4'>{data}</p>
            </div>

            <div>
                <Navbar>
                    <Container>
                        <Nav className="fs-5 hover  navbar-light bg-light">
                            <Nav.Link href="/"><Button variant="outline-dark">
                                <strong>Home</strong>
                            </Button></Nav.Link>
                            <Nav.Link href="/about"><Button variant="outline-dark" >
                                <strong>About Us</strong>
                            </Button></Nav.Link>
                            <Nav.Link href="/user"><Button variant="outline-dark">
                                <strong>Customer</strong>
                            </Button></Nav.Link>
                            <Nav.Link href="/contact"><Button variant="outline-dark">
                                <strong>Contact Us</strong>
                            </Button></Nav.Link>

                        </Nav>
                    </Container>
                </Navbar>
            </div>
        </div>
    );
};

export default Header
